import React, { Component } from "react";
import Button from "../Button";
import "./style.css";

export default class ControllerButton extends Component {
  state = {
    time: this.props.time
  };

  start = (time) => {
    setInterval(() => {
        let hour = Math.floor(time / 3600);
        let minute = Math.floor((time/ 60) % 60);
        let seconds = time % 60; 
        console.log(` ${hour} : ${minute} : ${seconds}`)
        this.setState({
            time: this.state.time + 1
        });
    },1000);
  }

  pause = (time) => {
    console.log('pause', time)
  }

  restart = (time) => {
    console.log('restart', time)
  }

  render() {
    const { time } = this.state;
    console.log('from controllerbutton', time)
    return (
      <div className="inLine">
          <Button action={this.start} time={time}> Start </Button> 
          <Button action={this.pause} time={time}> Pause </Button> 
          <Button action={this.restart} time={time}> Restart </Button> 
      </div>
    );
  }
}
