import React, { Component } from "react";
import Button from "../Button";
import "./style.css";

export default class ControllerButton extends Component {
  state = {
    time: this.props.time,
    interval: 0
  };

  start = () => {
    let interval = setInterval(() => {
        const time = this.state.time;
        let hour = Math.floor(time / 3600);
        let minute = Math.floor((time/ 60) % 60);
        let seconds = time % 60; 
        console.log(` ${hour} : ${minute} : ${seconds}`)
        this.setState({
            time: this.state.time + 1
        });
    },1000);

    this.setState({
        interval: interval
    })
  }

  pause = () => {
    clearInterval(this.state.interval);
  }

  restart = () => {
    this.setState({
        time: 0
    })
  }

  handleChange = () => {
   this.props.getTime(this.state.time);
  }

  render() {
    return (
      <div className="inLine">
          <Button action={this.start} getTime={this.handleChange}> Start </Button> 
          <Button action={this.pause} getTime={this.handleChange}> Pause </Button> 
          <Button action={this.restart} getTime={this.handleChange}> Restart </Button> 
      </div>
    );
  }
}
