import React, { Component } from "react";
import Button from "../Button";
import "./style.css";

export default class ControllerButton extends Component {

  render() {
    const getTime = this.props;
    return (
      <div className="inLine">
          <Button action="start" getTime={getTime}> Start </Button> 
          <Button action="pause" getTime={getTime}> Pause </Button> 
          <Button action="restart" getTime={getTime}> Restart </Button> 
      </div>
    );
  }
}
