import React, { Component } from "react";
import "./style.css";

export default class Button extends Component {

  state = {
    time: this.props.time,
    interval: 0
  };

  start = () => {
    let interval = setInterval(() => {
        this.setState({
            time: this.state.time + 1
        });
    },1000);

    this.setState({
        interval: interval
    })
  }

  pause = () => {
    clearInterval(this.state.interval);
  }

  restart = () => {
    this.setState({
        time: 0
    })
  }


  render() {
    const { children, getTime } = this.props;
    const { time } = this.state;
    return (
      <button onClick={ () => getTime(time)} >
          {children}
      </button>
    );
  }
}
